// Extraer email y password
const email = msg.payload?.email?.trim();
const password = msg.payload?.password?.trim();

if (!email || !password) {
    msg.statusCode = 400;
    msg.payload = { error: 'Faltan email o password' };
    return msg;
}

// Guardamos los valores originales para chequear doctor después
msg.loginEmail = email;
msg.loginPassword = password;

// Query inicial para usuarios
msg.topic = `SELECT id_user AS id_user, name, email 
             FROM users 
             WHERE email='${email}' AND password=SHA2('${password}',256) 
             LIMIT 1`;
msg.step = 'check_user';
return msg;

if (msg.step === 'check_user') {
if (msg.payload.length > 0) {
const u = msg.payload[0];
msg.payload = {
success: true,
role: 'User',
user: { id: u.id_user, name: u.name, email: u.email },
token: 'fake-token-user'
};
return [msg, null];
}

// No encontrado → buscar doctor usando email/password originales
msg.step = 'check_doctor';
msg.topic = `SELECT id_doctor AS id_doctor, name, email, role 
             FROM doctors 
             WHERE email='${msg.loginEmail}' AND password=SHA2('${msg.loginPassword}',256) 
             LIMIT 1`;
return [null, msg];

}

if (msg.step === 'check_doctor') {
if (msg.payload.length > 0) {
const d = msg.payload[0];
msg.payload = {
success: true,
role: 'Doctor', // Aseguramos que el dashboard cargue correctamente
user: { id: d.id_doctor, name: d.name, email: d.email, specialty: d.role },
token: 'fake-token-doctor'
};
return msg;
}

// No encontrado ni usuario ni doctor
msg.statusCode = 401;
msg.payload = { error: 'Usuario o contraseña incorrectos' };
return msg;

}
