// Extraer email y password
const email = msg.payload?.email?.trim();
const password = msg.payload?.password?.trim();

if (!email || !password) {
    msg.statusCode = 400;
    msg.payload = { error: 'Faltan email o password' };
    return msg;
}

msg.loginEmail = email;
msg.loginPassword = password;

// Query inicial para pacientes
msg.topic = `SELECT id_patient AS id_user, dni, name, email 
             FROM patients 
             WHERE email='${email}' AND password=SHA2('${password}',256) 
             LIMIT 1`;
msg.step = 'check_patient';
return msg;

if (msg.step === 'check_patient') {
    if (msg.payload.length > 0) {
        const u = msg.payload[0];
        msg.payload = {
            success: true,
            role: 'Patient',
            user: { id: u.id_user, dni: u.dni, name: u.name, email: u.email },
            token: 'fake-token-patient'
        };
        return [msg, null];
    }

    // No encontrado → buscar doctor usando email/password originales
    msg.step = 'check_doctor';
    msg.topic = `SELECT id_doctor, doctor_code, name, email, id_clinic 
                 FROM doctors 
                 WHERE email='${msg.loginEmail}' AND password=SHA2('${msg.loginPassword}',256) 
                 LIMIT 1`;
    return [null, msg];
}

if (msg.step === 'check_doctor') {
    if (msg.payload.length > 0) {
        const d = msg.payload[0];
        msg.payload = {
            success: true,
            role: 'Doctor',
            user: { id: d.id_doctor, doctor_code: d.doctor_code, name: d.name, email: d.email, clinic_id: d.id_clinic },
            token: 'fake-token-doctor'
        };
        return msg;
    }

    // No encontrado ni paciente ni doctor
    msg.statusCode = 401;
    msg.payload = { error: 'Usuario o contraseña incorrectos' };
    return msg;
}

// Este nodo confirma que recibimos el Base64 y el token.
const base64Image = msg.payload.image_base64;
const token = msg.payload.token;

if (!base64Image) {
    // Si no hay imagen, salta un error y cancela el flujo
    node.error("Error: No se encontró la imagen Base64 en el payload.");
    return null;
}

// 1. **Saltamos la llamada a la IA**
// 2. Creamos una respuesta SIMULADA, como si la IA hubiera respondido
msg.payload = {
    "responses": [
        {
            "labelAnnotations": [
                { "description": "Dermatitis", "score": 0.95 },
                { "description": "Psoriasis", "score": 0.60 },
                { "description": "Piel sana", "score": 0.05 }
            ]
        }
    ]
};

// Guardamos datos que podríamos necesitar
msg.client_token = token;

// Imprimimos una confirmación de la recepción
node.warn(`[SIMULACIÓN] Imagen Base64 recibida. Longitud: ${base64Image.length} caracteres.`);

return msg;

// 1. Capturamos la respuesta SIMULADA
const ia_response = msg.payload;

// 2. IMPRIMIR los datos COMPLETOs en el Debug de Node-RED (¡Tu requisito cumplido!)
node.warn("--- Conclusiones de la IA SIMULADAS Recibidas (Debug Output) ---");
node.warn(JSON.stringify(ia_response, null, 2));

// 3. Extracción y formateo para el cliente
const labels = ia_response.responses && ia_response.responses[0] && ia_response.responses[0].labelAnnotations;

let finalSummary = "No se encontraron conclusiones de la IA.";

if (labels && labels.length > 0) {
    finalSummary = labels.map(label => ({
        description: label.description,
        score: (label.score * 100).toFixed(2) + '%'
    }));
}

// 4. Preparamos el payload final para enviar de vuelta al navegador
msg.payload = {
    success: true,
    message: "Análisis simulado completado: Se leyó la foto correctamente.",
    ai_conclusions: finalSummary
};

// 5. Establecer el código de estado HTTP 200 (OK)
msg.statusCode = 200;

return msg;
